      // }

      // state = newState;


      // console.log("newState", newState);

      console.log("state.basketNumber", state.basketNumber);
      if (state.basketNumber == 0) {
        let cart = {
          id: action.payload.id,
          amount: 1,
          title: action.payload.name,
          image: action.payload.image,
          price: action.payload.price,
          description: action.payload.description,
          tags: action.payload.tags,
        };
        state.basket.push(cart);
        console.log("state.basket", state.basket.length);
      } else {
        let check = false;
        state.basket.map((item, key) => {
          if (item.id == action.payload.id) {
            state.basket[key].amount++;
            check = true;
          }
        });
        if (!check) {
          let _cart = {
            id: action.payload.id,
            amount: 1,
            title: action.payload.name,
            image: action.payload.image,
            price: action.payload.price,
            description: action.payload.description,
            tags: action.payload.tags,
          };
          state.basket.push(_cart);

        }
      }

      state= {...state,basketNumber:state.basketNumber+1};
      // return {
      //   ...state,
      //   basketNumber: state.basketNumber + 1,
      // };

    },

  },
});

export const {
  addBasket,
  removeBasket,
  setBasket,
  resetBasket,
} = basketSlice.actions;

export default basketSlice.reducer;
