import { createSlice } from "@reduxjs/toolkit";

export const basketSlice = createSlice({
  name: "basket",
  initialState: {
    value: [],
  },
  reducers: {
    addBasket: (state, action) => {
      state.value = state.value.push(action.payload);
    },
    removeBasket: (state, action) => {
      state.value = state.value.filter((p) => p.id !== action.payload);
    },
    setBasket: (state, action) => {
      state.value = action.payload;
    },
    resetBasket: (state) => {
      state.value = [];
    },
    addBasketNonAmount: (state, action) => {
      console.log(state.value)
      return state.value.map(item => {
        if(item.id !== action.payload.id) {
          return item
        }
        return {
          ...item,added: true}

      // const val = state.value.find((p) => {if(p.id === action.payload.id)return p});
      // console.log(val);


      // state.value = state.value.push({action.payload, amount: 1})
      }
      );
    },
  },
});

export const { addBasket, removeBasket, setBasket, resetBasket, addBasketNonAmount } =
  basketSlice.actions;

export default basketSlice.reducer;
