import React,{ useEffect, useState} from 'react'
import { Button, Stack } from 'react-bootstrap';
import styles from '../styles/styles';


export default function Product(product, index, basket, setBasket) {


    const [inc, setInc] = React.useState(0);

        useEffect(() => {
          console.log(basket);
        }, [basket]);
    const decrement = () => {
        if (inc > 0) setInc(inc - 1);
        setBasket(basket.filter((item) => item.id !== product.id));
    };
    const increment = () => {
        if (inc >= 0) setInc(inc + 1);
        setBasket([...basket, product]);
    };
  return (
    <div key={index} class="card" style={{ width: "18rem", margin: "10px" }}>
      <img
        class="card-img-top"
        src={product.product.image}
        alt={product.product.title}
      />
      <div class="card-body">
        <h5 class="card-title">{product.product.title}</h5>
        <p class="card-text">{product.description}</p>

        <div
          className="d-flex flex-row justify-content-around align-items-center"
          style={{ width: "70%", marginLeft: "15%" }}
        >
          <button className="btn btn-secondary" onClick={decrement}>
            Çıkar
          </button>
          <div>{inc}</div>
          <div className="btn btn-success" onClick={increment}>
            Ekle
          </div>
        </div>
      </div>
    </div>
  );
}
