import { createSlice } from "@reduxjs/toolkit";
let newState;
export const basketSlice = createSlice({
  name: "basket",
  initialState:[],
  reducers: {
    addBasket: (state, action) => {
      state = state.push(action.payload);
    },
    removeBasket: (state, action) => {
      state = state.filter((p) => p.id !== action.payload);
    },
    setBasket: (state, action) => {
      state = action.payload;
    },
    resetBasket: (state) => {
      state = [];
    },
    addBasket: (state, action) => {

      newState = [...state];
      if (newState.find((p) => p.id === action.payload.id)) {
        newState.find((p) => p.id === action.payload.id).amount++;
      } else {
        newState.push(action.payload, { amount: 1 });
      }
      state = newState;
      return state;

    },

  },
});

export const {
  addBasket,
  removeBasket,
  setBasket,
  resetBasket,
} = basketSlice.actions;

export default basketSlice.reducer;
