import { createSlice } from "@reduxjs/toolkit";

export const basketSlice = createSlice({
  name: "basket",
  initialState: {
    value: [],
  },
  reducers: {
    addBasket: (state, action) => {
      state.value = state.value.push(action.payload);
    },
    removeBasket: (state, action) => {
      state.value = state.value.filter((p) => p.id !== action.payload);
    },
    setBasket: (state, action) => {
      state.value = action.payload;
    },
    resetBasket: (state) => {
      state.value = [];
    },
    addBasket: (state, action) => {
      if (state.value.find((p) => p.id === action.payload.id)) {
        console.log("var")
      } else {
        console.log("yok")
      }
      // return {
      //   ...state,
      //   value: [
      //     ...state.value,
      //     {
      //       id: action.payload.id,
      //       title: action.payload.title,
      //       description: action.payload.description,
      //       image: action.payload.image,
      //       tags: action.payload.tags,
      //       price: action.payload.price,
      //       amount: 1,
      //     },
      //   ],
      // };
    },
    addBasketHasAmount: (state, action) => {
      const basketCheck = state.value.find((p) => p.id === action.payload.id);
      const basketCurrent = state.value.filter(
        (p) => p.id !== action.payload.id
      );
      basketCheck.amount = basketCheck.amount ? 1 : basketCheck.amount + 1;
      return {
        ...state,
        value: [...basketCurrent, basketCheck],
      };
    },
  },
});

export const {
  addBasket,
  removeBasket,
  setBasket,
  resetBasket,
  addBasketNonAmount,
  addBasketHasAmount,
} = basketSlice.actions;

export default basketSlice.reducer;
