import React from 'react'


export default function Basket({basket, setBasket, inc,setInc,product}) {


 const decrement = () => {
   const basketCheck = basket.find((p) => p.id === product.id);
   const basketCurrent = basket.filter((p) => p.id !== product.id);
   basketCheck.amount = basketCheck.amount - 1;
   if (basketCheck.amount === 0) {
     setBasket([...basketCurrent]);
   } else {
     setBasket([...basketCurrent, basketCheck]);
   }
   if (inc > 0) setInc(inc - 1);
 };

 const increment = () => {
   const basketCheck = basket.find((p) => p.id === product.id);
   if (basketCheck) {
     setBasket(
       basket.map((p) =>
         p.id === product.id
           ? { ...basketCheck, amount: basketCheck.amount + 1 }
           : p
       )
     );
   } else {
     setBasket([...basket, { ...product, amount: 1 }]);
   }
   setInc(inc + 1);
 };




