import React,{ useEffect, useState} from 'react'
import { Button, Stack } from 'react-bootstrap';
import styles from '../styles/styles';


export default function Product({product, basket, setBasket,inc,setInc}) {

  console.log("basket", basket)
  const basketIndex = basket.findIndex((b) => b.id === product.id);
  const decrement = () => {

    const basketCheck = basket.find((p) => p.id === product.id);
    const basketCurrent = basket.filter((p) => p.id !== product.id);
    basketCheck.amount = basketCheck.amount - 1;
    if (basketCheck.amount === 0) {
      setBasket([...basketCurrent]);
    } else {
      setBasket([...basketCurrent, basketCheck]);
    }
    if (inc > 0) setInc(inc - 1);

  };


  const increment = () => {
    const basketCheck = basket.find((p) => p.id === product.id);
    if (basketCheck) {
      setBasket(
        basket.map((p) =>
          p.id === product.id
            ? { ...basketCheck, amount: basketCheck.amount + 1 }
            : p
        )
      );
    } else {
      setBasket([...basket, { ...product, amount: 1 }]);
    }
    setInc(inc + 1);
  };


  return (
    <div className="card" style={{ width: "18rem", margin: "10px" }}>
      <img
        className="card-img-top"
        src={product.image}
        alt={product.title}
      />
      <div className="card-body">
        <h5 className="card-title">{product.title}</h5>
        <p className="card-text">{product.description}</p>

        <div
          className="d-flex flex-row justify-content-around align-items-center"
          style={{ width: "70%", marginLeft: "15%" }}
        >
          <button className="btn btn-secondary" onClick={decrement}>
            Çıkar
          </button>
          <div>{basket[0].amount}</div>
          <div className="btn btn-success" onClick={increment}>
            Ekle
          </div>
        </div>
      </div>
    </div>
  );
}
