    display: "flex",
    flexDirection: "column",
    alignItems: "center",
    justifyContent: "center",
  },
  bodyContainer: {
    display: "flex",
    flexDirection: "row",
    alignItems: "center",
      justifyContent: "center",
    width: "100%",
    },
    headerContainer: {
        display: "flex",
        flexDirection: "row",
        alignItems: "center",
        justifyContent: "center",
        width: "100%",
        height: "100px",
        backgroundColor: "lightblue",
    },

  leftContainer: {
    display: "flex",
    flexDirection: "column",
    alignItems: "center",
    justifyContent: "center",
    fontSize: "calc(10px + 2vmin)",
    color: "black",
    width: "80%",
    border: "1px solid black",
    backgroundColor: "red",
    height: "100vh",
  },
  rightContainer: {
    display: "flex",
    flexDirection: "column",
    alignItems: "center",
    justifyContent: "center",
    fontSize: "calc(10px + 2vmin)",
    height: "100vh",
    color: "black",
    width: "20%",
    border: "1px solid black",
    backgroundColor: "blue",
  },
};
export default styles;
