import React,{useState} from 'react'
import { Button, Stack } from 'react-bootstrap';
import styles from '../styles/styles';


export default function Product(product, index) {
    const [inc, setInc] = React.useState(1);
  return (
    <div key={index} class="card" style={{ width: "18rem", margin: "10px" }}>
      <img
        class="card-img-top"
        src={product.product.image}
        alt={product.product.title}
      />
      <div class="card-body">
        <h5 class="card-title">{product.product.title}</h5>
        <p class="card-text">{product.description}</p>

        <div
          className="d-flex flex-row justify-content-around align-items-center"
          style={{ width: "70%", marginLeft: "15%" }}
        >
          <button className="btn btn-secondary" onClick={() => setInc(inc - 1)}>
            Çıkar
          </button>
          <div>{inc}</div>
          <div className="btn btn-success" onClick={() => setInc(inc + 1)}>
            Ekle
          </div>
        </div>
      </div>
    </div>
  );
}
