import React from 'react'
import styles from '../styles/styles'

export default function Header({ total }) {
  return (
    <div
      className="fs-2 text-white"
      style={
        total > 500 ? { backgroundColor: "red" } : { backgroundColor: "green" }
      }
    >
      {total > 500 ? "Harcamayı tamamlamak için " : "Harcayacağınız "}
      <span className="fs-2" style={styles.span}>
        {500 - total} TL
      </span>{" "}
      {total > 500 ? "`ye ihtiyacınız var." : "kaldı."}
    </div>
  );



}
