import { createSlice } from "@reduxjs/toolkit";

export const basketSlice = createSlice({
  name: "basket",
  initialState: {
    basket: [],
    total: 0,
  },
  reducers: {
    addBasket: (state, action) => {
      state = state.push(action.payload);
    },
    removeBasket: (state, action) => {
      state = state.filter((p) => p.id !== action.payload);
    },
    setBasket: (state, action) => {
      state = action.payload;
    },
    resetBasket: (state) => {
      state = [];
    },
    addBasket: (state, action) => {

      if (state.basket.find((p) => p.id === action.payload.id)) {
        // const index = state.basket.findIndex((p) => p.id === action.payload.id);
        // state.basket[index].amount = state.basket[index].amount + 1;
        console.log("var")
      } else {
        let cart = {
          id: action.payload.id,
          amount: 1,
          title: action.payload.title,
          image: action.payload.image,
          price: action.payload.price,
          description: action.payload.description,
          tags: action.payload.tags,
        };
        state.basket[state.basket.length] = cart;
        // state.basket.push(bcart);
        console.log("yok")
      }


      let _total = 0;
      state.basket.map((item, key) => {
        _total = _total + item.price * item.amount;
      });

      state.total = _total;

      return { basket: state.basket, total: state.total };


    },
    },
});

export const {
  addBasket,
  removeBasket,
  setBasket,
  resetBasket,
} = basketSlice.actions;

export default basketSlice.reducer;
