import { createSlice } from "@reduxjs/toolkit";

export const basketSlice = createSlice({
  name: "basket",
  initialState: {
    value: [],
  },
  reducers: {
    addBasket: (state, action) => {
      state.value = state.value.push(action.payload);
    },
    removeBasket: (state, action) => {
      state.value = state.value.filter((p) => p.id !== action.payload);
    },
    setBasket: (state, action) => {
      state.value = action.payload;
    },
    resetBasket: (state) => {
      state.value = [];
    },
    addBasketNonAmount: (state, action) => {
      const newState = { ...state, amount: 1 };
      newState.value = newState.value.push(action.payload);
      console.log("newState : ", newState)


      return newState;
    },
    addBasketHasAmount: (state, action) => {


      // newState.find((p) => p.id === action.payload.id).amount = newState.findIndex((p) => p.id === action.payload.id).amount + 1;
      // state.value = newState;
    }
  },
});

export const {
  addBasket,
  removeBasket,
  setBasket,
  resetBasket,
  addBasketNonAmount,
  addBasketHasAmount,
} = basketSlice.actions;

export default basketSlice.reducer;
