import { createSlice } from "@reduxjs/toolkit";

export const basketSlice = createSlice({
  name: "basket",
  initialState:[],
  reducers: {
    addBasket: (state, action) => {
      state = state.push(action.payload);
    },
    removeBasket: (state, action) => {
      state = state.filter((p) => p.id !== action.payload);
    },
    setBasket: (state, action) => {
      state = action.payload;
    },
    resetBasket: (state) => {
      state = [];
    },
    addBasket: (state, action) => {

      var newState = [...state];
      if (newState.find((p) => p.value.id === action.payload.id)) {
        const index=newState.findIndex((p) => p.value.id === action.payload.id);

        newState[index].amount = newState[index].amount + 1;

      } else {
        newState=newState.push({value:action.payload,amount:1});
      }

      state = newState;


      console.log("state",state);

    },

  },
});

export const {
  addBasket,
  removeBasket,
  setBasket,
  resetBasket,
} = basketSlice.actions;

export default basketSlice.reducer;
