import React from "react";
import styles from "../styles/styles";
import products from "../products.json";
import Basket from "./basket";

export default function Basketlist({ basket, setBasket, inc, setInc, product, total }) {




    return (
      <>
        <div className="d-flex flex-start">
          <div
            className="p-2 justify-center align-items-center fs-2 fw-3 "
            style={{
              position: "fixed",
              top: "10vh",
              backgroundColor: "white",
              width: "100%",
              borderBottom: "1px solid gray",
              borderTop: "1px solid gray",
            }}
          >
            <p
              style={{
                marginLeft: "3%",
                alignItems: "center",
                justifyContent: "center",
                color: "black",
                fontSize: "40px",
                float: "left",
                fontWeight: "bold",
              }}
            >
              Sepet DetaylarÄ±
            </p>
          </div>
        </div>

        <div
          className="d-flex flex-column"
          style={{ paddingBottom: "45%", paddingTop: "25%" }}
        >
          {basket.map((basket, index) => (
            <Basket
              key={index}
              basket={basket}
              setBasket={setBasket}
              product={product}
              inc={inc}
              setInc={setInc}
              total={total}
            />
          ))}

          <div className="d-flex flex-end">
            <div
              className="p-2 justify-center align-items-center fs-2 fw-3 "
              style={{
                position: "fixed",
                bottom: "0",
                backgroundColor: "green",
                width: "100%",
                borderTop: "1px solid gray",
              }}
            >
              <p
                style={{
                  marginLeft: "3%",
                  alignItems: "center",
                  justifyContent: "center",
                  color: "white",
                  fontSize: "40px",
                  float: "left",
                  fontWeight: "bold",
                }}
              >
                Toplam {total} TL
              </p>
            </div>
          </div>
        </div>
      </>
    );
}
